var makeBookApp=angular.module("makeBookApp",["ngRoute"]);makeBookApp.controller("CardController",["$scope","$http",function(t,n){t.msg="no message",t.contacts=[],n.get("js/contacts.js").success(function(n){t.rawData=n,t.rawLength=n.contacts.length,t.rawObj=n.contacts[0].name,t.contacts=n.contacts}),t.addContact=function(){t.contacts.push({name:t.createname,number:t.createnumber,picture:"http://placehold.it/200x200"}).then(function(){t.createname=""})},t.contactDelete=function(n){t.contacts.splice(n,1)}}]),makeBookApp.controller("EditController",["$scope",function(t){t.nested="unconfirmed",t.editContactName=t.contact.name,t.editContactNumber=t.contact.number,t.editSubmit=function(n){t.contacts[n]={name:t.editContactName,number:t.editContactNumber,picture:"http://placehold.it/200x200"}},t.contactEditConfirm=function(){t.nested="confirm"}}]);